$(document).ready(function () {
  // form validation
  const validator = $("#form-dang-tin").validate({
    errorClass: "error-label c-main mt-2",
    rules: {
      image: {
        required: true,
        number: true,
        min: 1,
      },
      title: {
        required: true,
      },
      "description-value": {
        required: true,
        minlength: 100,
      },
      price: {
        required: true,
      },
      location: {
        required: true,
      },
    },
    messages: {
      image: {
        required: "Hình ảnh không được để trống",
        min: "Hình ảnh không được để trống",
      },
      title: {
        required: "Tiêu đề không được bỏ trống",
      },
      "description-value": {
        required: "Mô tả không được để trống",
        minlength: "Mô tả ít hơn 100 ký tự",
      },
      price: {
        required: "Giá không được để trống",
      },
      location: {
        required: "Tỉnh thành không được để trống",
      },
    },
    highlight: function (element, errorClass) {
      $(element).removeClass(errorClass);
    },
  });

  $("#form-dang-tin").steps({
    headerTag: "h4",
    bodyTag: "section",
    transitionEffect: "fade",
    titleTemplate: '<div class="title">#title#</div>',
    autoFocus: true,
    enableAllSteps: true,
    transitionEffectSpeed: 300,
    labels: {
      next: '<i class="zmdi zmdi-arrow-right"></i>',
      previous: "Trở lại",
      finish: "Đăng tin",
    },
    // onStepChanging: function (event, currentIndex, newIndex) {
    //   return $("#form-dang-tin").valid();
    // },
  });
});
