$.fn.tabs = function () {
  var n = $(this);
  n.on("click", ".tab-item", function () {
    debugger;
    if (!$(this).hasClass("active")) {
      n.find(".tab-item").removeClass("active");
      $(this).addClass("active");
      var t = $(this).data("id");
      $("#" + t)
        .siblings()
        .hide();
      $("#" + t).show();
    }
  });
};
