$(document).ready(function () {
  var switchBaseConfig = {
    on_txt: "",
    off_txt: "",
    on_color: "#de1818",
  };

  const provinceList = [
    {
      id: 1,
      text: "Hà Nội",
    },
    {
      id: 2,
      text: "Hải Phòng",
    },
  ];

  const transactioTypeList = [
    {
      id: 1,
      text: "Chuyển khoản",
    },
    {
      id: 2,
      text: "Nạp tiền qua MoMo",
    },
  ];

  const wizard = $("#thong-tin-tai-khoan").steps({
    headerTag: "h4",
    bodyTag: "section",
    transitionEffect: "fade",
    titleTemplate: '<div class="title">#title#</div>',
    autoFocus: true,
    enableAllSteps: true,
    transitionEffectSpeed: 300,
  });

  $("#tinh-thanh").select2({
    data: provinceList,
    placeholder: "Tỉnh/Thành phố bán xe",
    width: "100%",
  });

  $("#loai-giao-dich").select2({
    data: transactioTypeList,
    placeholder: "Loại giao dịch",
    width: "100%",
  });

  const switchSelector = $("input[type=checkbox].lcs_check");
  lc_switch(switchSelector, switchBaseConfig); // init switch

  $("#menu-account-desktop").on("click", ".account-menu-item", function (e) {
    // di chuyển đến step
    const menuItem = $(e.currentTarget);
    const stepId = menuItem.data("step");
    wizard.steps("setStep", stepId);

    // active menu item
    $(".account-menu-item").removeClass("active");
    menuItem.addClass("active");
  });
});
